import React from 'react';
import C from './Users.module.css';
import * as axios from 'axios';
import userPhoto from '../../../assets/pictures/ava.png'

class Users extends React.Component {
    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {
            this.props.setUsers(response.data.items);
        });
    }

    render() {

        let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize);

        let pages = [];
for ( let i=1; i <= pagesCount; i++){
    pages.push(i);
}
        return (
            <div>
                <div>
                    {pages.map( p => {
                       return <span className={this.props.currentPage === p &&  C.selectedPage} onClick={() => { this.props.stCurrentPage }}>{p}</span>
                    })}
                </div>
                {
                    this.props.users.map(u => <div key={u.id}>
                        <span>
                            <div>
                                <img src={u.photos.small != null ? u.photos.small : userPhoto} alt="" className={C.ava} />
                            </div>
                            <div>
                                {u.followed ? <button onClick={() => { this.props.unfollow(u.id) }}>Unfollow</button> : <button onClick={() => { this.props.follow(u.id) }}>Follow</button>
                                }                        </div>
                        </span>
                        <span>
                            <span>
                                <div>{u.name}</div>
                                <div>{u.status}</div>
                            </span>
                            <span>
                                <div>{"u.location.country"}</div>
                                <div>{"u.location.city"}</div>
                            </span>

                        </span>
                    </div>
                    )
                }
            </div>
        )
    }
}

export default Users;
