import React from 'react';
import C from './Messages.module.css'
import MessageItem from './Message1/Message1';
import DialogItem from './DialogItem/DialogItem';

const Messages = (props) => {

let userElem = props.messagesState.usersData.map( (dialog)=> <DialogItem name={dialog.name} id={dialog.id} />);


let messageElem = props.messagesState.messageData.map( (message)=> <MessageItem message={message.message}/>);

let newMessageElement = React.createRef();

let Send = () => {
    let text = newMessageElement.current.value;
    alert(text);
}

    return (
        <article className={C.article}>
            <div className={C.users}>
                {userElem} 
            </div>
            <div className={C.messages}>
                <div>{messageElem}</div>
                 <textarea ref={newMessageElement}></textarea>
                <button onClick={Send}>Send</button>
                <div>
                    <div></div>
                    <div></div>
                </div>

            </div>
        </article>
    )
}

export default Messages;
