import { updateNewMessageBodyCreator, sendMessageCreator } from '../../../Redux/message-reducer';
import Messages from './Messages';

const MessagesContainer = (props) => {

let messagesState = props.store.getState().dialogsPage;

let onSendMessageClick = () => {
  props.store.dispatch(sendMessageCreator()); 
}
let onNewMessageChange = (body) => {
   props.store.dispatch(updateNewMessageBodyCreator(body));
}

    return (<Messages updateNewMessageBody={onNewMessageChange} 
                      sendMessage={onSendMessageClick}
                      dialogsPage={messagesState}/>)
}

export default MessagesContainer;
