import React from 'react';

import { updateNewMessageBodyCreator, sendMessageCreator } from '../../../Redux/message-reducer';
import Messages from './Messages';
import StoreContext from '../../../StoreContext';
import { connect } from 'react-redux'

const MessagesContainer = () => {

  return (<StoreContext.Consumer>
    {
      (store) => {
        let messagesState = store.getState().dialogsPage;
        let onSendMessageClick = () => {
          store.dispatch(sendMessageCreator());
        }
        let onNewMessageChange = (body) => {
          store.dispatch(updateNewMessageBodyCreator(body));
        }
        return (
          <Messages updateNewMessageBody={onNewMessageChange}
            sendMessage={onSendMessageClick}
            dialogsPage={store.getState().messagesState} />
        )
      }
    }
  </StoreContext.Consumer>


  )
}

let mapStateToProps = (state) => {
  return {
    dialogsPage: state.dialogsPage
  }
}
let mapDispatchToProps = (dispatch) => {
  return {
    updateNewMessageBody: () => {
      dispatch(sendMessageCreator());
    },
    sendMessage: (body) => {
      dispatch(updateNewMessageBodyCreator(body));
    }
  }
}

const SuperDialogsContainer = connect(mapStateToProps, mapDispatchToProps)(Messages);

export default MessagesContainer;
