const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';

let initialState = {

    users: [
        // { id: 1, followed: false, fullName: 'Johan Jam', status: "I'm old porn-actor", location: { city: 'Kiev', country: 'Ukraine' } },
        // { id: 2, followed: false, message: 'Jake Morrison', status: "A moore moooorrre", location: { city: 'Berlin', country: 'Germany' } },
        // { id: 3, followed: true, message: 'Lisa Duke', status: "Duke?", location: { city: 'Nikopol', country: 'Ukraine' } },
        // { id: 4, followed: false, message: 'Luisa Dick', status: "Penis", location: { city: 'Norway', country: 'Oslo' } },
    ],
    newPostText: ''
}

const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                // users: [...state.users],
                users: state.users.map(u => {
                    if (u.id === action.userID) {
                        return { ...u, followed: true }
                    }
                    return u
                })
            }


        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userID) {
                        return { ...u, followed: false }
                    }
                    return u
                })
            }
            case SET_USERS: {
                return {...state, users: [...state.users, ...action.users]}
            }

        default:
            return state;
    }

}

export const followAC = (userID) => {
    return { type: FOLLOW, userID }
}
export const unfollowAC = (userID) => {
    return { type: UNFOLLOW, userID }
}
export const setUsersAC = (users) => {
    return { type: SET_USERS, users }
}
export default usersReducer;
