import React from 'react';
import C from './Users.module.css';
import userPhoto from '../../../assets/pictures/ava.png'


let Users = (props) => {
    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);

    let pages = [];
for ( let i=1; i <= pagesCount; i++){
pages.push(i);
}
    return (
        <div>
            <div className={C.selectPages}>
                {pages.map(p => {
                    return <span className={props.currentPage === p && C.selectedPage}
                        onClick={(e) => { props.onPageChanged(p); }}>{p}</span>
                })}
            </div>
            {
                props.users.map(u => <div key={u.id}>
                    <span>
                        <div>
                            <img src={u.photos.small != null ? u.photos.small : userPhoto} alt="" className={C.ava} />
                        </div>
                        <div>
                            {u.followed ? <button onClick={() => { props.unfollow(u.id) }}>Unfollow</button> : <button onClick={() => { props.follow(u.id) }}>Follow</button>
                            }                        </div>
                    </span>
                    <span>
                        <span>
                            <div>{u.name}</div>
                            <div>{u.status}</div>
                        </span>
                        <span>
                            <div>{"u.location.country"}</div>
                            <div>{"u.location.city"}</div>
                        </span>

                    </span>
                </div>
                )
            }
        </div>
    )

}

export default Users;
