import React from 'react';
import C from './Users.module.css'


let Users = (props) => {

    if (props.users.length === 0) {
        axios.get("https://social-network.samuraijs.com/api/1.0/users").then(response => {
            props.setUsers(
                [
                    { id: 1, photoUrl: 'https://sevstar.net/wp-content/uploads/2018/04/%D1%81%D0%B2%D0%B8%D0%BD%D1%8C%D1%8F-200%D1%85200.jpg', followed: false, fullName: 'Johan Jam', status: "I'm old porn-actor", location: { city: 'Kiev', country: 'Ukraine' } },
                    { id: 2, photoUrl: 'https://sevstar.net/wp-content/uploads/2018/04/%D1%81%D0%B2%D0%B8%D0%BD%D1%8C%D1%8F-200%D1%85200.jpg', followed: false, fullName: 'Jake Morrison', status: "A moore moooorrre", location: { city: 'Berlin', country: 'Germany' } },
                    { id: 3, photoUrl: 'https://sevstar.net/wp-content/uploads/2018/04/%D1%81%D0%B2%D0%B8%D0%BD%D1%8C%D1%8F-200%D1%85200.jpg', followed: true, fullName: 'Lisa Duke', status: "Duke?", location: { city: 'Nikopol', country: 'Ukraine' } },
                    { id: 4, photoUrl: 'https://sevstar.net/wp-content/uploads/2018/04/%D1%81%D0%B2%D0%B8%D0%BD%D1%8C%D1%8F-200%D1%85200.jpg', followed: false, fullName: 'Luisa Dick', status: "Penis", location: { city: 'Oslo', country: 'Norway' } }
                ]
            )

        })

    }
    return (
        <div>
            {
                props.users.map(u => <div key={u.id}>
                    <span>
                        <div>
                            <img src={u.photoUrl} alt="" className={C.ava} />
                        </div>
                        <div>
                            {u.followed ? <button onClick={() => { props.unfollow(u.id) }}>Unfollow</button> : <button onClick={() => { props.follow(u.id) }}>Follow</button>
                            }                        </div>
                    </span>
                    <span>
                        <span>
                            <div>{u.fullName}</div>
                            <div>{u.status}</div>
                        </span>
                        <span>
                            <div>{u.location.country}</div>
                            <div>{u.location.city}</div>
                        </span>

                    </span>
                </div>)
            }
        </div>
    )
}

export default Users;
